import { saveSelection, runEnabledFeatures } from '/modules/consent/consentApi.js';
import { insertContentFromPack } from '/modules/loader/contentManager.js';

const acc = document.getElementById('consent-accept');
const more = document.getElementById('consent-more');

if (acc) acc.addEventListener('click', async () => {
  await saveSelection({ cookies: true });
  await runEnabledFeatures();
  const el = document.getElementById('consent-quick'); if (el) el.remove();
});

if (more) more.addEventListener('click', async () => {
  // Quick UI entfernen
  const el = document.getElementById('consent-quick'); if (el) el.remove();

  // Extended Tileset laden
  const extendedTileset = await fetch('./packs/global/tilesets/policy/extended.json').then(r => r.json());
  insertContentFromPack(extendedTileset, 'consent-container');
});
